v class="site1">                                <img src="images/<?php echo $sites['image'] ?>" title="<?php echo $sites['nomSite'] ?>" alt="<?php echo $sites['nomSite']; ?>"/>                                <div class='contenuSites'>                                    <p class='titreSite'><?php echo $sites['nomSite'] ?></p>                                    <p class='lienVersDetails'>En Savoir +</p>                                </div>                            </div>                        </a>                    </div>                    <?php                }                ?>                <!-- end of loop -->            </div>            <div class="clear"></div>            <!-- Projet en cours -->            <div class="headerselection">                <p>Les projets en cours</p>            </div>            <div class='sites'>                <!-- loop to display "in progress" sites -->                <?php                $requeteSites = execute_requete("SELECT * FROM realisations WHERE etat='inProgress'");                while ($sites = $requeteSites->fetch_assoc()) {                    ?>                    <div class='ficheSite'>                        <a href="<?php echo $sites['lien'] ?>">                            <div class="site1">                                <img src="images/<?php echo $sites['image'] ?>" title="<?php echo $sites['nomSite'] ?>" alt="<?php echo $sites['nomSite']; ?>"/>                                <div class='contenuSites'>                                    <p class='titreSite'><?php echo $sites['nomSite'] ?></p>                                    <p class='lienVersDetails'>En Savoir +</p>                                </div>                            </div>                        </a>                    </div>                    <?php                }                ?>                <!-- end of loop -->            </div>        <?php    }    ?>    </div><!-- stop #selection --